import React, { useState } from "react";

import { Grid } from "@material-ui/core";
import { Paper } from "@material-ui/core";

import { Technology } from "../../../../store/technology";
import styled from "styled-components";
import logo from "../../../../images/react_logo.png";

import { ViewTechnologies } from "../index";
import { Id } from "../../../../models/types";
import TechnologyContent from "./Content";

const gridView = {
  column: {
    container: {
      direction: "column" as any,
      spacing: 3,
    },
    item: {
      width_lg: 12 as any,
      width_xs: 12 as any,
    },
  },
  row: {
    container: {
      direction: "column" as any,
      spacing: 3,
    },
    item: {
      width_lg: 4 as any,
      width_xs: 12 as any,
    },
  },
};

const PaperItemStyled = styled(Paper)`
  position: relative;
  padding: 10px;
  width: 100%;
  height: 300px;
  box-sizing: border-box;
`;

const GridItemStyled = styled(Grid)`
  height: 100%;
  transition: all 300ms ease 0ms;
`;

const ImageGridStyled = styled(Grid)<{ moved: boolean }>`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  ${(props) => props.moved 
    ? ``
  :``
  }
`;

export interface TechnologyDetailsProps {
  tech: Technology;
  remove: (techId: Id) => Promise<void>;
  view: ViewTechnologies;
  action: boolean;
  handleOpen: (technology?: Technology) => void;
}

const TechnologyDetails: React.FC<TechnologyDetailsProps> = ({
  tech,
  remove,
  view,
  action,
  handleOpen,
}) => {
  const [hovered, setHovered] = useState<boolean>(false);

  return (
    <GridItemStyled
      item
      lg={gridView[view].item.width_lg}
      xs={gridView[view].item.width_xs}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      <PaperItemStyled>
        <Grid container spacing={2} alignItems="center">
          <ImageGridStyled item>
            <img src={logo} alt="logo" style={{ width: "100px" }} />
          </ImageGridStyled>
          <Grid item>
            <TechnologyContent
              tech={tech}
              remove={remove}
              action={action}
              handleOpen={handleOpen}
            />
          </Grid>
        </Grid>
      </PaperItemStyled>
    </GridItemStyled>
  );
};

export default TechnologyDetails;
