import React, { useContext, useState } from "react";
import { useForm } from "react-hook-form";

import {
  Button,
  Dialog,
  DialogTitle,
  Grid,
  TextField,
} from "@material-ui/core";
import styled from "styled-components";
import AddLogo from "../../global/AddLogo";
import { StoreTechnologiesContext } from "../../../store/technology";

const GridContainerStyled = styled(Grid)`
  padding: 10px;
`;

const GridItemStyled = styled(Grid)`
  margin-bottom: 10px;
`;

export interface CreateTechnologyProps {
  handleClose: () => void;
  open: boolean;
}

const CreateTechnology: React.FC<CreateTechnologyProps> = ({
  handleClose,
  open,
}) => {
  const technologiesStore = useContext(StoreTechnologiesContext);
  const { add } = technologiesStore;

  const { handleSubmit, register, errors } = useForm();
  const onSubmit = (values: any) => {console.log(values);
    add(values);
  };

  const [image, setImage] = useState<any>();
  return (
    <Dialog
      onClose={handleClose}
      aria-labelledby="simple-dialog-title"
      open={open}
    >
      <DialogTitle>Add Technology</DialogTitle>
      <form onSubmit={handleSubmit(onSubmit)}>
        <GridContainerStyled
          container
          direction="column"
          alignItems="center"
          justify="space-evenly"
        >
          <GridItemStyled item>
            <AddLogo image={image} setImage={setImage} />
          </GridItemStyled>
          <GridItemStyled item>
            <TextField
              label="Name"
              inputProps={{
                name: "name",
                ref: register({
                  required: "Required",
                  maxLength: 255,
                  minLength: 2,
                }),
              }}
              helperText={errors.name && "Title must have at least 2 signs!"}
              error={Boolean(errors.name)}
            />
          </GridItemStyled>
          <GridItemStyled item>
            <TextField
              label="Desription"
              multiline
              inputProps={{
                name: "description",
                ref: register({
                  maxLength: 255,
                }),
              }}
            />
          </GridItemStyled>
          <GridItemStyled item>
            <Button variant="contained" color="primary" type="submit">
              ADD
            </Button>
          </GridItemStyled>
        </GridContainerStyled>
      </form>
    </Dialog>
  );
};

export default CreateTechnology;
