import React, { useContext, useEffect, useState } from "react";
import { observer } from "mobx-react";

import AppsIcon from "@material-ui/icons/Apps";
import ViewStreamIcon from "@material-ui/icons/ViewStream";
import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import EditIcon from "@material-ui/icons/Edit";
import AddIcon from "@material-ui/icons/Add";
import { Grid, IconButton, Typography } from "@material-ui/core";
import { Paper } from "@material-ui/core";

import {
  StoreTechnologiesContext,
  Technology,
} from "../../../store/technology";
import SectionComponent from "../Section";
import SpeedDialComponent from "../../global/SpeedDial";
import TechnologyForm from "./form";
import styled from "styled-components";
import { parseStyledBoolean } from "../../../models/global";

enum View {
  column = "column",
  row = "row",
}

const gridView = {
  column: {
    container: {
      direction: "column" as any,
      spacing: 3,
    },
    item: {
      width_lg: 12 as any,
      width_xs: 12 as any,
    },
  },
  row: {
    container: {
      direction: "column" as any,
      spacing: 3,
    },
    item: {
      width_lg: 3 as any,
      width_xs: 12 as any,
    },
  },
};

const PaperItemStyled = styled(Paper)`
  position: relative;
  padding: 10px;
`;

const GridItemStyled = styled(Grid)`
  height: 100%;
  transition: all 300ms ease 0ms;
`;

const ActionGridStyled = styled(Grid)<{ active?: string }>`
  ${(props) => (props.active ? `display: block` : `display: none`)}
`;

const Technologies: React.FC = observer(() => {
  const technologiesStore = useContext(StoreTechnologiesContext);
  const { technologies, fetch, remove } = technologiesStore;

  useEffect(() => {
    fetch();
  }, [fetch]);

  const [edit, setEdit] = useState<Technology | boolean>();
  const [action, setAction] = useState<boolean>(false);
  const [view, setView] = useState<View>(View.column);

  const handleInactivate = () => {
    setAction(false);
  };

  const handleActivate = () => {
    setAction(true);
  };

  const handleClose = () => {
    setEdit(false);
  };

  const handleOpen = (technology?: Technology) => {
    setEdit(technology ? technology : true);
  };

  const actions = [
    { icon: <AddIcon onClick={() => handleOpen()} />, name: "Add" },
    { icon: <EditIcon onClick={handleActivate} />, name: "Edit" },
    {
      icon: <ViewStreamIcon onClick={() => setView(View.column)} />,
      name: "Show columns",
    },
    { icon: <AppsIcon onClick={() => setView(View.row)} />, name: "Show rows" },
  ];

  return (
    <SectionComponent title={"Technologies"}>
      <Grid container spacing={2} alignItems="center" justify="space-around">
        {technologies.map((tech) => (
          <GridItemStyled
            item
            key={tech.id}
            lg={gridView[view].item.width_lg}
            xs={gridView[view].item.width_xs}
          >
            <PaperItemStyled>
              <Grid container spacing={2} alignItems="center">
                <Grid item>
                  <Grid
                    container
                    justify=""
                    alignItems="center"
                    spacing={1}
                  >
                    <Grid item>
                      <Typography color="primary" variant="h5">
                        {tech.name}
                      </Typography>
                    </Grid>
                    <ActionGridStyled item active={parseStyledBoolean(action)}>
                      <IconButton size="small" onClick={() => handleOpen(tech)}>
                        <EditIcon fontSize="small" />
                      </IconButton>
                      <IconButton size="small" onClick={() => remove(tech.id)}>
                        <DeleteForeverIcon fontSize="small" />
                      </IconButton>
                    </ActionGridStyled>
                  </Grid>
                </Grid>
                <Grid item>
                  <Typography color="primary" variant="body1">
                    {tech.description}
                  </Typography>
                </Grid>
              </Grid>
            </PaperItemStyled>
          </GridItemStyled>
        ))}
      </Grid>
      <SpeedDialComponent
        actions={actions}
        blocked={action}
        unBlock={handleInactivate}
      />
      <TechnologyForm
        open={Boolean(edit)}
        technology={typeof edit === "object" ? edit : undefined}
        handleClose={handleClose}
      />
    </SectionComponent>
  );
});

export default Technologies;
