import React, { useContext, useEffect, useState } from "react";
import { observer } from "mobx-react";

import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import EditIcon from "@material-ui/icons/Edit";
import AddIcon from "@material-ui/icons/Add";
import {
  IconButton,
  List,
  ListItem,
  ListItemSecondaryAction,
  ListItemText,
} from "@material-ui/core";

import {
  StoreTechnologiesContext,
  Technology,
} from "../../../store/technology";
import SectionComponent from "../Section";
import SpeedDialComponent from "../../global/SpeedDial";
import TechnologyForm from "./form";
import styled from "styled-components";

const ActionStyled = styled(ListItemSecondaryAction)<{ active?: boolean }>`
  ${(props) => (props.active ? `display: block` : `display: none`)}
`;

const Technologies: React.FC = observer(() => {
  const technologiesStore = useContext(StoreTechnologiesContext);
  const { technologies, fetch, remove } = technologiesStore;

  useEffect(() => {
    fetch();
  }, [fetch]);

  const [open, setOpen] = useState<boolean>(false);
  const [edit, setEdit] = useState<Technology | undefined>();
  const [action, setAction] = useState<boolean>(false);

  const handleInactivate = () => {
    setAction(false);
  };

  const handleActivate = () => {
    setAction(true);
  };

  const handleClose = () => {
    setEdit(undefined);
    setOpen(false);
  };

  const handleOpen = (technology?: Technology) => {
    setEdit(technology);
    setOpen(true);
  };

  const actions = [
    { icon: <AddIcon onClick={() => handleOpen()} />, name: "Add" },
    { icon: <EditIcon onClick={handleActivate} />, name: "Edit" },
  ];

  console.log('ds');
  return (
    <SectionComponent title={"Technologies"}>
      <List>
        {technologies.map((tech) => (
          <ListItem key={tech.id}>
            <ListItemText primary={tech.name} secondary={tech.description} />
            <ActionStyled
              active={action ? action : undefined}
            >
              <IconButton onClick={() => handleOpen(tech)}>
                <EditIcon />
              </IconButton>
              <IconButton onClick={() => remove(tech.id)}>
                <DeleteForeverIcon />
              </IconButton>
            </ActionStyled>
          </ListItem>
        ))}
      </List>
      <SpeedDialComponent
        actions={actions}
        blocked={action}
        unBlock={handleInactivate}
      />
      <TechnologyForm open={open} technology={edit} handleClose={handleClose} />
    </SectionComponent>
  );
});

export default Technologies;
