import React from "react";
import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import EditIcon from "@material-ui/icons/Edit";
import { Grid, IconButton, Typography } from "@material-ui/core";
import { Paper } from "@material-ui/core";

import { Technology } from "../../../../store/technology";
import styled from "styled-components";
import { parseStyledBoolean } from "../../../../models/global";
import logo from "../../../../images/react_logo.png";

import { ViewTechnologies } from "../index";
import { Id } from "../../../../models/types";

const gridView = {
  column: {
    container: {
      direction: "column" as any,
      spacing: 3,
    },
    item: {
      width_lg: 12 as any,
      width_xs: 12 as any,
    },
  },
  row: {
    container: {
      direction: "column" as any,
      spacing: 3,
    },
    item: {
      width_lg: 3 as any,
      width_xs: 12 as any,
    },
  },
};

const PaperItemStyled = styled(Paper)`
  position: relative;
  padding: 10px;
`;

const GridItemStyled = styled(Grid)`
  height: 100%;
  transition: all 300ms ease 0ms;
`;

const ActionGridStyled = styled(Grid)<{ active?: string }>`
  ${(props) => (props.active ? `display: block` : `display: none`)}
`;

export interface TechnologyDetailsProps {
  tech: Technology;
  remove: (techId: Id) => Promise<void>;
  view: ViewTechnologies;
  action: boolean;
  handleOpen: (technology?: Technology) => void;
}

const TechnologyDetails: React.SFC<TechnologyDetailsProps> = ({
  tech,
  remove,
  view,
  action,
  handleOpen,
}) => {
  return (
    <GridItemStyled
      item
      lg={gridView[view].item.width_lg}
      xs={gridView[view].item.width_xs}
    >
      <PaperItemStyled>
        <Grid container spacing={2} alignItems="center">
          <Grid item>
            <img src={logo} alt="logo" style={{ width: "50px" }} />
          </Grid>
          <Grid container spacing={2} alignItems="center">
            <Grid item>
              <Grid item>
                <Grid
                  container
                  justify="space-between"
                  alignItems="center"
                  spacing={1}
                >
                  <Grid item>
                    <Typography color="primary" variant="h5">
                      {tech.name}
                    </Typography>
                  </Grid>
                  <ActionGridStyled item active={parseStyledBoolean(action)}>
                    <IconButton size="small" onClick={() => handleOpen(tech)}>
                      <EditIcon fontSize="small" />
                    </IconButton>
                    <IconButton size="small" onClick={() => remove(tech.id)}>
                      <DeleteForeverIcon fontSize="small" />
                    </IconButton>
                  </ActionGridStyled>
                </Grid>
              </Grid>
              <Grid item>
                <Typography color="primary" variant="body1">
                  {tech.description}
                </Typography>
              </Grid>
            </Grid>
          </Grid>
        </Grid>
      </PaperItemStyled>
    </GridItemStyled>
  );
};

export default TechnologyDetails;
