import React, { ReactNode } from "react";

import AddIcon from '@material-ui/icons/Add';
import SpeedDial from '@material-ui/lab/SpeedDial';
import SpeedDialIcon from '@material-ui/lab/SpeedDialIcon';
import SpeedDialAction from '@material-ui/lab/SpeedDialAction';

import styled from "styled-components";

const SpeedDialStyled = styled(SpeedDial)`
  position: absolute;
  right: 10px;
  top: 10px;
`;

const IconStyled = styled(AddIcon)<{ blocked?: boolean }>`
  transition: transform 0.3;
  ${(props) => (props.blocked ? `transform: rotate` : `display: none`)}
`;

type SpeedDialComponentProps = {
    actions: {
        icon: ReactNode,
        name: string
    }[];
    blocked?: boolean;
}

const SpeedDialComponent: React.FC<SpeedDialComponentProps> = ({actions, blocked}) => {

  const [open, setOpen] = React.useState(false);

  const handleClose = () => {
    setOpen(false);
  };

  const handleOpen = () => {
    setOpen(true);
  };

  return (
      <SpeedDialStyled
          ariaLabel="SpeedDial example"
          icon={<SpeedDialIcon />}
          onClose={handleClose}
          onOpen={handleOpen}
          open={open}
          direction={'down'}
        >
      {actions.map((action) => (
                  <SpeedDialAction
                    key={action.name}
                    icon={action.icon}
                    tooltipTitle={action.name}
                    onClick={handleClose}
                  />
          ))}
        </SpeedDialStyled>
  );
};

export default SpeedDialComponent;
