import React, { useContext, useEffect, useState } from "react";
import { observer } from "mobx-react";

import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import EditIcon from "@material-ui/icons/Edit";
import AddIcon from "@material-ui/icons/Add";
import {
  Button,
  IconButton,
  List,
  ListItem,
  ListItemSecondaryAction,
  ListItemText,
} from "@material-ui/core";

import { StoreTechnologiesContext } from "../../../store/technology";
import SectionComponent from "../Section";
import SpeedDialComponent from "../../global/SpeedDial";
import CreateTechnology from "./create";
import styled from "styled-components";

const ActionStyled = styled(ListItemSecondaryAction)<{ active: boolean }>`
  ${(props) => (props.active ? `display: block` : `display: none`)}
`;

const ButtonActionStyled = styled(Button)<{ active: boolean }>`
  ${(props) => (props.active ? `display: block` : `display: none`)}
`;

const Technologies: React.FC = observer(() => {
  const technologiesStore = useContext(StoreTechnologiesContext);
  const { technologies, fetch } = technologiesStore;

  useEffect(() => {
    fetch();
  }, [fetch]);

  const [open, setOpen] = useState<boolean>(false);
  const [action, setAction] = useState<boolean>(false);

  const handleInactivate = () => {
    setAction(false);
  };

  const handleActivate = () => {
    setAction(true);
    console.log(action);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleOpen = () => {
    setOpen(true);
  };

  const actions = [
    { icon: <AddIcon onClick={handleOpen} />, name: "Add" },
    { icon: <EditIcon onClick={handleActivate} />, name: "Edit" },
  ];

  console.log(action);
  return (
    <SectionComponent title={"Technologies"}>
      <ButtonActionStyled
        active={Boolean(action)}
        onClick={handleInactivate}
        variant="outlined"
        color="primary"
      >
        Submit
      </ButtonActionStyled>
      <List>
        {technologies.map((tech) => (
          <ListItem key={tech.id}>
            <ListItemText primary={tech.name} secondary={tech.description} />
            <ActionStyled active={Boolean(action)}>
              <IconButton>
                <EditIcon />
              </IconButton>
              <IconButton>
                <DeleteForeverIcon />
              </IconButton>
            </ActionStyled>
          </ListItem>
        ))}
      </List>
      <SpeedDialComponent actions={actions} />
      <CreateTechnology open={open} handleClose={handleClose} />
    </SectionComponent>
  );
});

export default Technologies;
