import React from "react";
import imageCompression from 'browser-image-compression';

import AddAPhotoIcon from "@material-ui/icons/AddAPhoto";
import ClearIcon from "@material-ui/icons/Clear";

import { TournamentCreateLogoTextFieldStyled } from "../../../styled/styledTournamentForm";
import { ButtonRemoveLogoStyled } from "../../../styled/styledButtons";
import { LogoContainerStyled, LogoStyled } from "../../../styled/styledLayout";


export const TournamentCreateLogoLabelStyled = styled.label`
  text-align: center;
  color: #7e6714;
  background-color: rgba(0, 0, 0, 0.4);
  box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14),
    0 3px 1px -2px rgba(0, 0, 0, 0.12), 0 1px 5px 0 rgba(0, 0, 0, 0.2);
  margin-right: 10px;
  display: flex;
  padding: 7px;
  border-radius: 5px;
`;

const AddLogo = ({ image, setImage }) => {
    const handleChangeImage = async (e) => {
        const image = e.target.files[0];
        const options = {
            maxSizeMB: 0.1,
            maxWidthOrHeight: 500,
            useWebWorker: true
        }
        try {
            const compressedFile = await imageCompression(image, options);
            setImage(compressedFile);
        } catch (error) {
            console.log(error);
        }
    };

    const onRemoveImage = () => {
        setImage(null);
    };

    const getUrl = () => {
        if (image) {
            return URL.createObjectURL(image);
        } else {
            return undefined;
        }
    };

    const imgUrl = getUrl();
    return (
        <LogoContainerStyled>
            <TournamentCreateLogoTextFieldStyled
                type="file"
                name="file"
                id="file"
                onChange={handleChangeImage}
            />
            <label htmlFor="file">
                <LogoStyled src={imgUrl}>
                    {imgUrl ? null : <AddAPhotoIcon />}
                </LogoStyled>
            </label>
            {imgUrl ? (
                <ButtonRemoveLogoStyled onClick={onRemoveImage}>
                    <ClearIcon fontSize="small" />
                </ButtonRemoveLogoStyled>
            ) : null}
        </LogoContainerStyled>
    );
};

export default AddLogo;
