import React, { useContext, useEffect, useState } from "react";
import { observer } from "mobx-react";

import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import EditIcon from "@material-ui/icons/Edit";
import AddIcon from "@material-ui/icons/Add";
import {
  Grid,
  IconButton,
  List,
  ListItem,
  ListItemSecondaryAction,
  ListItemText,
  Typography,
} from "@material-ui/core";

import {
  StoreTechnologiesContext,
  Technology,
} from "../../../store/technology";
import SectionComponent from "../Section";
import SpeedDialComponent from "../../global/SpeedDial";
import TechnologyForm from "./form";
import styled from "styled-components";
import { parseStyledBoolean } from "../../../models/global";

const ActionStyled = styled(ListItemSecondaryAction)<{ active?: string }>`
  ${(props) => (props.active ? `display: block` : `display: none`)}
`;

const Technologies: React.FC = observer(() => {
  const technologiesStore = useContext(StoreTechnologiesContext);
  const { technologies, fetch, remove } = technologiesStore;

  useEffect(() => {
    fetch();
  }, [fetch]);

  const [edit, setEdit] = useState<Technology | boolean>();
  const [action, setAction] = useState<boolean>(false);

  const handleInactivate = () => {
    setAction(false);
  };

  const handleActivate = () => {
    setAction(true);
  };

  const handleClose = () => {
    setEdit(false);
  };

  const handleOpen = (technology?: Technology) => {
    setEdit(technology ? technology : true);
  };

  const actions = [
    { icon: <AddIcon onClick={() => handleOpen()} />, name: "Add" },
    { icon: <EditIcon onClick={handleActivate} />, name: "Edit" },
  ];

  return (
    <SectionComponent title={"Technologies"}>
      <List>
        {technologies.map((tech) => (
          <ListItem key={tech.id}>
            <ListItemText primary={tech.name} secondary={tech.description} />
            <ActionStyled active={parseStyledBoolean(action)}>
              <IconButton onClick={() => handleOpen(tech)}>
                <EditIcon />
              </IconButton>
              <IconButton onClick={() => remove(tech.id)}>
                <DeleteForeverIcon />
              </IconButton>
            </ActionStyled>
          </ListItem>
        ))}
      </List>
      <Grid container direction="column">
        {technologies.map((tech) => (
          <Grid item key={tech.id}>
            <Grid container>
              <Grid item>
                <Typography color="primary">{tech.name}</Typography>
              </Grid>
              <Grid item>
                <Typography color="secondary">{tech.description}</Typography>
              </Grid>
            </Grid>
          </Grid>
        ))}
      </Grid>
      <SpeedDialComponent
        actions={actions}
        blocked={action}
        unBlock={handleInactivate}
      />
      <TechnologyForm
        open={Boolean(edit)}
        technology={typeof edit === "object" ? edit : undefined}
        handleClose={handleClose}
      />
    </SectionComponent>
  );
});

export default Technologies;
